import React from "react"
import './Home.css'
import NewQuiz from './NewQuiz'

class AdminDashboard extends React.Component{
    constructor(){
     super()
     this.state={
         createQuiz : false
     }
    }

    createNewQuiz = () => {
        this.setState({createQuiz:true})
    }
    render() {
        console.log(this.state.createQuiz)
        return (

            <div>

                <div style={{backgroundColor:'#A2D2FF'}}>
                <h1 className="admin-dashboard-heading" style={{color:"#FEF9EF",marginTop:'0px'}}>Welcome to Admin Dashboard!</h1>
                <br></br><br></br>
                <hr></hr>
                </div>
                <br></br><br></br>
                <button style={{width:'200px',height:'70px',marginLeft:'3px'}} className="button-27" onClick={this.createNewQuiz}>
                    <span style={{fontSize:'25px'}}>Create Quiz</span>
                </button>
                <br></br><br></br>
                <h2>Upcoming Quizzes : </h2>
                {this.state.createQuiz?<NewQuiz></NewQuiz>:'No quizes available'}
            </div>
        )
    }
}

export default AdminDashboard
