import React from "react"
import Question from './Question'


class NewQuiz extends React.Component{

    constructor() {
        super()
        this.state = {
            quiz : {
                id : '',
                title : '',
                questions : [{id:'',question:'',correctAnswer:'',options:[],timeLimit : '',points:''},{id:'',question:'',correctAnswer:'',incorrectAnswer:[],timeLimit : '',points:''}],
            },
            quizzes : [],
            questionNum  : 1,
            addQuestion : false

        }

    }

    handleSubmit = () => {
        this.props.handleCloseModal()
    }



    addQuestion = () => {
        var questionNumber = this.state.questionNum
        questionNumber = questionNumber + 1
        this.setState({questionNum:questionNumber})
        this.setState({addQuestion:true})
        return (
          <Question questionNumber={this.state.questionNum}></Question>
        )

      }

    render(){




        return(
            <div>

              <h1>Quiz creater</h1>
              <br></br><br></br>
              <label>Quiz Title : </label>
              <input></input>
              <br></br><br></br><br></br>
              <Question></Question>
              <br></br><br></br><br></br>
              {this.state.addQuestion?<Question></Question>:''}
              <button onClick={this.addQuestion}>Add question</button>
              <br></br><br></br>
              <button>Save quiz</button>
              <br></br><br></br>
              <button>Cancel quiz</button>


            </div>
        )
    }

}

export default NewQuiz
