import React from "react"
import Dialog from '@mui/material/Dialog';
import Button from '@mui/material/Button';
import { DialogActions, DialogContent, DialogTitle } from "@mui/material";

class NewQuiz extends React.Component{

    constructor() {
        super()
        this.state = {
            quiz : {
                id : '',
                title : '',
                questions : [{id:'',question:'',correctAnswer:'',incorrectAnswer:[]},{id:'',question:'',correctAnswer:'',incorrectAnswer:[]}],
                timeLimit : ''
            },
            quizzes : []
        }

    }

    handleSubmit = () => {
        this.props.handleCloseModal()
    }

    handleClose = () => {
        this.props.handleCloseModal()
    }

    render(){
        return(
            <div>

            <Dialog open={this.props.createQuiz} onClose={this.handleClose} maxWidth='lg'>
                <DialogTitle>Create Quiz</DialogTitle>
                <DialogContent></DialogContent>
                <DialogActions>
                    <Button onClick={this.handleClose}>Submit</Button>
                    <Button onClick={this.handleClose}>Cancel</Button>
                </DialogActions>

            </Dialog>


            </div>
        )
    }

}

export default NewQuiz
